import React, { Component } from 'react';
import { Link, Redirect } from 'react-router-dom';
import classes from './admin.module.css';
import Navbars from '../Navbar/navbar2';
import Sidebar from '../sidebar/sidebar';
import Charts from '../Admin/charts';
import Chartlines from '../Admin/chartlines';
import Footer from '../Footer/footer';

class Admin extends Component {
	constructor(props) {
		super(props);
		let token = localStorage.getItem('token');

		let loggedin = true;
		if (token == null) {
			loggedin = false;
		}

		this.state = {
			loggedin
		};
	}

	state = {
		sidebartoggler: false
	};

	drawerbuttons = () => {
		this.setState((prevState) => {
			return { sidebartoggler: !prevState.sidebartoggler };
		});
	};

	render() {
		if (this.state.loggedin === false) {
			return <Redirect to="/" />;
		}

		let sidebar;
		if (this.state.sidebartoggler) {
			sidebar = <Sidebar />;
		}

		return (
			<div>
				<header>
					<Navbars burgerbutton={this.drawerbuttons} />
					{sidebar}
				</header>
				<main className={classes.container}>
					<div className={classes.row}>
						<h1>
							Welcome back, <span>Admin</span>
						</h1>
					</div>
					<div className={classes.body}>
						<div className={classes.chart}>
							<Latesthits />
						</div>
						<div className={classes.chartlines}>
							<Chartlines />
						</div>
					</div>
				</main>
				<footer style={{bottom:"0",position:"absolute",width:"100%"}}>
					<Footer />
				</footer>
			</div>
		);
	}
}

export default Admin;
