import React, { Component } from 'react';
import { Line } from 'react-chartjs-2';
import classes from './charts.module.css';

class Charts extends Component {
	constructor(props) {
		super(props);

		this.state = {
			data: {
				labels: [ 'January', 'February', 'March', 'April', 'May', 'June', 'July' ],
				datasets: [
					{
						label: 'Latest Hits',
						// backgroundColor: 'rgba(255,0,255,0.75)',
						data: [ 88, 68, 79, 57, 50, 55, 70 ],
						labeldata: [ 'Hits' ],
						borderColor: 'rgba(126, 214, 223,1.0)',
						pointBackgroundColor: '#fff',
						fill: false,
						pointBorderColor: 'rgba(220,220,220,1)',
						pointBackgroundColor: '#fff',

						fontColor: '#fff'
					},

					{
						label: 'Popular Hits',
						// backgroundColor: '',
						color: 'white',
						data: [ 33, 45, 37, 21, 55, 74, 69 ],
						borderColor: 'rgba(255, 121, 121,1.0)',
						fill: false
					},
					{
						label: 'Featured',
						// backgroundColor: 'rgba(0,155,0,0.85)',
						color: 'white',
						data: [ 44, 19, 38, 46, 85, 66, 79 ],
						borderColor: 'rgba(153, 128, 250,1.0)',
						fill: false
					}
				]
			}
			
        };
        (options: {
            legend: {
                labels:{
                fontColor: '#fff',
            }
            }
        })
	}

	setGradientColor = (canvas, color) => {
		const ctx = canvas.getContext('2d');
		const gradient = ctx.createLinearGradient(0, 0, 0, 0);
		gradient.addColorStop(0, color);
		gradient.addColorStop(0.95, 'rgba(133,122,255,0.5)');
		return gradient;
	};

	getCharData = (canvas) => {
		const data = this.state.data;
		if (data.datasets) {
			let colors = [ 'rgba(255,0,255,0.75)', 'rgba(0,255,0,0.75)', 'rgba(125,0,255,0.75)' ];
			data.datasets.forEach((set, i) => {
				// set.label = this.setGradientColor(canvas, colors[i]);

				set.boderWidth = 1;
			});
		}
		return data;
	};
	render() {
		return (
			<div style={{ width: '600', height: '550', color: '#fff' }}>
				<h3 style={{ color: '#fff', fontSize: '1.1rem', fontWeight: 700, marginBottom: '30px' }}>
					Latest Hits
				</h3>
				<Line options={{ responsive: true }} data={this.getCharData} />
			</div>
		);
	}
}

export default Charts;
