import React, { Component } from 'react';
import { Line } from 'react-chartjs-2';
import classes from './charts.module.css';

class Charts extends Component {
	constructor(props) {
		super(props);

		this.state = {
			data: {
				labels: [ 'January', 'February', 'March', 'April', 'May', 'June', 'July' ],
				datasets: [
					{
						label: 'Latest Hits',
						backgroundColor: 'rgba(255,0,255,0.75)',
						color: 'white',
						data: [ 4, 5, 1, 10, 32, 2, 12 ]
					},
					{
						label: 'Popular Hits',
						backgroundColor: 'rgba(0,255,0,0.75)',
						color: 'white',
						data: [ 14, 5, 10, 21, 32, 12, 12 ]
					},
					{
						label: 'Featured',
						backgroundColor: 'rgba(0,155,0,0.85)',
						color: 'white',
						data: [ 20, 10, 30, 40, 80, 50, 70, 60, 90 ]
					}
				]
			}
		};
	}
	render() {
		return (
			<div style={{ width: '600', height: '550' }}>
				<h3 style={{ color: '#fff', fontSize: '1.1rem', fontWeight: 700, marginBottom: '30px' }}>
					Latest Hits
				</h3>
				<Line options={{ responsive: true }} data={this.getC} />
			</div>
		);
	}
}

export default Charts;
