import React, { Component } from 'react';
import { HorizontalBar } from 'react-chartjs-2';
import classes from './charts.module.css';

class Chartlines extends Component {
	constructor(props) {
		super(props);

		this.state = {
			data: {
				labels: [ 'Red', 'Aqua', 'Green', 'Yellow', 'purple', 'Orange', 'Blue' ],
				datasets: [
					{
						label: '# of Hits',
						// backgroundColor: 'rgba(255,0,255,0.75)',
						data: [ 33, 40, 28, 49, 58, 38, 44 ],
						backgroundColor: [
							'rgba(255, 77, 77,1.0)',
							'rgba(85, 239, 196,1.0)',
							'rgba(184, 233, 148,1.0)',
							'rgba(255, 242, 0,1.0)',
							'rgba(197, 108, 240,1.0)',
							'rgba(255, 159, 26,1.0)',
							'rgba(87, 95, 207,1.0)'
						],
						barPercentage: 0.25,
						fill: false,
						pointBorderColor: 'rgba(220,220,220,1)',
						pointBackgroundColor: '#fff',

						fontColor: '#fff',
						lineTension: 0
					}
				]
			}
		};
	}


	
	render() {
		return (
			<div
				style={{
					width: '500px',
					minHeight: '250px',
					height: '100%',
					maxHeight: '450px',
					color: 'red',
					padding: '40px'
				}}
			>
				<h3 style={{ color: '#fff', fontSize: '1.1rem', fontWeight: 700, marginBottom: '30px' }}>
					Performance
				</h3>
				<HorizontalBar
					options={{
						legend: {
							fullWidth: 'false',
							backgroundColor: 0,
							labels: {
								fontColor: '#fff'
							}
						},
						scales: {
							xAxes: [
								{
									ticks: {
										fontColor: '#fff'
									}
								}
							],
							yAxes: [
								{
									ticks: {
										fontColor: '#fff'
									}
								}
							]
						},
						responsive:true,
						title: {
							display: this.props.display,
							text: this.props.title
						}
					}}
					data={this.getCharData}
				/>
			</div>
		);
	}
}

export default Chartlines;
