import React, { Component } from 'react';
import { Line } from 'react-chartjs-2';
import classes from './charts.module.css';

class Charts extends Component {
	constructor(props) {
		super(props);

		this.state = {
			data: {
				labels: [ 'January', 'February', 'March', 'April', 'May', 'June', 'July' ],

				datasets: [
					{
						label: 'Latest Hits',
						// backgroundColor: 'rgba(255,0,255,0.75)',
						data: [ 88, 68, 79, 57, 50, 55, 70 ],
						labeldata: [ 'Hits' ],
						borderColor: 'rgba(126, 214, 223,1.0)',
						pointRadius: 0,
						fill: false,
						fontColor: '#fff'
					},

					{
						label: 'Popular Hits',
						// backgroundColor: '',
						color: 'white',
						data: [ 33, 45, 37, 21, 55, 74, 69 ],
						borderColor: 'rgba(255, 121, 121,1.0)',
						pointRadius: 0,
						fill: false
					},
					{
						label: 'Featured',
						// backgroundColor: 'rgba(0,155,0,0.85)',
						color: 'white',
						data: [ 44, 19, 38, 46, 85, 66, 79 ],
						borderColor: 'rgba(153, 128, 250,1.0)',
						pointRadius: 0,
						fill: false
					}
				]
			}
		};
	}

	setGradientColor = (canvas, color) => {
		const ctx = canvas.getContext('2d');
		const gradient = ctx.createLinearGradient(0, 0, 465, 232);
		gradient.addColorStop(0, color);
		gradient.addColorStop(0.95, 'rgba(133,122,255,0.5)');
		return gradient;
	};

	getCharData = (canvas) => {
		const data = this.state.data;
		if (data.datasets) {
			let colors = [ 'rgba(255,0,255,0.75)', 'rgba(0,255,0,0.75)', 'rgba(125,0,255,0.75)' ];
			data.datasets.forEach((set, i) => {
				// set.label = this.setGradientColor(canvas, colors[i]);

				set.borderWidth = 3;
			});
		}
		return data;
	};
	render() {
		return (
			<div
				style={{
					width: '500px',
					minHeight: '250px',
					height: '100%',
					maxHeight: '450px',
					color: 'red',
					padding: '40px'
				}}
			>
				<h3 style={{ color: '#fff', fontSize: '1.1rem', fontWeight: 700, marginBottom: '30px' }}>
					Latest Hits
				</h3>
				<Line
					data={{
						labels: this.props.labels,
						datasets: this.props.data
					}}
					options={{
						legend: {
							fullWidth:'false',
							backgroundColor:0,
							labels:{
								fontColor:"#fff",
							}
						},
						scales: {
							yAxes: [
								{
									ticks: {
										max: this.props.maxY,
										min: 0,
										stepSize: 3
									}
								}
							]
						},
						title: {
							display: this.props.display,
							text: this.props.title
						}
					}}
					data={this.getCharData}
				/>
			</div>
		);
	}
}

export default Charts;
