import React, { Component } from 'react';
import { HorizontalBar } from 'react-chartjs-2';
import classes from './charts.module.css';

class Chartlines extends Component {
	constructor(props) {
		super(props);

		this.state = {
			data: {
				labels: [ 'Red', 'Aqua', 'Green', 'Yellow', 'purple', 'Orange', 'Blue' ],
				datasets: [
					{
						label: '# of Hits',
						// backgroundColor: 'rgba(255,0,255,0.75)',
						data: [ 33, 40, 28, 49, 58, 38, 44 ],
						backgroundColor: [ 'Red', 'Aqua', 'Green', 'Yellow', 'purple', 'Orange', 'Blue' ],

						labeldata: [ 'Hits' ],
						borderColor: 'rgba(126, 214, 223,1.0)',
						pointBackgroundColor: '#fff',
						fill: false,
						pointBorderColor: 'rgba(220,220,220,1)',
						pointBackgroundColor: '#fff',

						fontColor: '#fff',
						lineThickness:0
					}
				]
			}
		};
	}

	setGradientColor = (canvas, color) => {
		const ctx = canvas.getContext('2d');
		const gradient = ctx.createLinearGradient(0, 0, 0, 0);
		gradient.addColorStop(0, color);
		gradient.addColorStop(0.95, 'rgba(133,122,255,0.5)');
		return gradient;
	};

	getCharData = (canvas) => {
		const data = this.state.data;
		if (data.datasets) {
			let colors = [ 'rgba(255,0,255,0.75)', 'rgba(0,255,0,0.75)', 'rgba(125,0,255,0.75)' ];
			data.datasets.forEach((set, i) => {
				// set.label = this.setGradientColor(canvas, colors[i]);

				set.borderWidth = 1;
			});
		}
		return data;
	};
	render() {
		return (
			<div
				style={{
					width: '500px',
					minHeight: '250px',
					height: '100%',
					maxHeight: '450px',
					color: 'red',
					padding: '40px'
				}}
			>
				<h3 style={{ color: '#fff', fontSize: '1.1rem', fontWeight: 700, marginBottom: '30px' }}>
					Performance
				</h3>
				<HorizontalBar options={{ responsive: true }} data={this.getCharData} />
			</div>
		);
	}
}

export default Chartlines;
